import React, { useEffect, useContext } from "react";
// Context
import ShowsContext from "../context/shows/showsContext";

// Components

const Singlepage = ({ match }) => {
  const { getSingleShow, singleShow } = useContext(ShowsContext);

  useEffect(() => {
    getSingleShow(match.params.id);

    // eslint-disable-next-line
  }, []);

  const removeTags = (text) => {
    if (text === null || text === "") {
      return false;
    } else {
      text = text.toString();
    }
    return text.replace(/(<([^>]+)>)/gi, "");
  };

  const handleBookTicket = () => {
    const formWindow = window.open("", "_blank");
    formWindow.document.write(`
      <html>
      <head>
        <title>Book Ticket</title>
      </head>
      <body>
        <h2>Book Ticket</h2>
        <!-- Add your booking form HTML code here -->
        <form>
          <!-- Form fields and elements go here -->
          <input type="text" placeholder="Name" />
          <input type="email" placeholder="Email" />
          <button type="submit">Submit</button>
        </form>
      </body>
      </html>
    `);
    formWindow.document.close();
  };

  return (
    <>
      <div className="singleshow">
        <img
          src={
            singleShow.image
              ? singleShow.image.medium
              : "https://www.publicdomainpictures.net/pictures/280000/velka/not-found-image-15383864787lu.jpg"
          }
          alt={singleShow.name}
        />
        <div className="singleshow__info">
          <h1>{singleShow.name}</h1>
          {singleShow.genres &&
            singleShow.genres.map((genre) => (
              <span key={genre} className="singleshow__genre">
                {genre}
              </span>
            ))}
          <button className="book book-tckt" onClick={handleBookTicket}>
            Book Ticket
          </button>
          <p>
            <strong>Status:</strong> {singleShow.status && singleShow.status}
          </p>
          <p>
            <strong>Rating:</strong>{" "}
            {singleShow.rating ? singleShow.rating.average : "No rating"}
          </p>
          <p>
            <strong>Official Site:</strong>{" "}
            {singleShow.officialSite ? (
              <a
                href={singleShow.officialSite}
                target="_blank"
                rel="noreferrer"
              >
                {singleShow.officialSite}
              </a>
            ) : (
              "No official site"
            )}
          </p>
          <p>{singleShow.summary && removeTags(singleShow.summary)}</p>
        </div>
      </div>
    </>
  );
};

export default Singlepage;
